import React, { useState } from 'react';
import axios from 'axios';

const AddComic = () => {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [coverImage, setCoverImage] = useState('');
  const [rating, setRating] = useState('');
  const [issueNumber, setIssueNumber] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const newComic = {
      title,
      author,
      cover_image: coverImage,
      rating,
      issueNumber,
    };

    axios.post('http://localhost:5000/books', newComic)
      .then(response => {
        alert('Comic added successfully!');
        // Optionally reset form fields here
        setTitle('');
        setAuthor('');
        setCoverImage('');
        setRating('');
        setIssueNumber('');
      })
      .catch(error => {
        console.error('There was an error adding the comic!', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Title:</label>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
      </div>
      <div>
        <label>Author:</label>
        <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} required />
      </div>
      <div>
        <label>Cover Image URL:</label>
        <input type="text" value={coverImage} onChange={(e) => setCoverImage(e.target.value)} required />
      </div>
      <div>
        <label>Rating:</label>
        <input type="number" value={rating} onChange={(e) => setRating(e.target.value)} required min="0" max="5" step="0.1" />
      </div>
      <div>
        <label>Issue Number:</label>
        <input type="text" value={issueNumber} onChange={(e) => setIssueNumber(e.target.value)} required />
      </div>
      <button type="submit">Add Comic</button>
    </form>
  );
};

export default AddComic;
